// Example: Access data from previous node
const input = $('accounts').first().json.username; // Or $('PreviousNodeName').item.json if you prefer explicit reference

// Initialize directory variables
let dir_video = "";
let dir_obj = "";
let dir_output = "";

// Apply multiple conditional branches
if (input === "paheli") {
  dir_video = "/home/node/shared/files/bgvideo";
  dir_obj = "/home/node/shared/files/diwali";
  // dir_output = "/home/node/shared/FACEBOOK/output";
} 
else if (input === "urban__raja") {
  dir_video = "/home/node/shared/files/bgvideo";
  dir_obj = "/home/node/shared/files/diwali";
  // dir_output = "/home/node/shared/FACEBOOK/output";
}
else if (input.category === "instagram") {
  dir_video = "/home/node/shared/INSTAGRAM/bgvideo";
  dir_obj = "/home/node/shared/INSTAGRAM/icons";
  dir_output = "/home/node/shared/INSTAGRAM/output";
} 

else {
  // For any unexpected or custom case
  dir_video = `/home/node/shared/files/${input.category}/bgvideo`;
  dir_obj = `/home/node/shared/files/${input.category}/icons`;
  dir_output = `/home/node/shared/files/${input.category}/output`;
}

// Return directories as output for next node
return [
  {
    json: {
      dir_video,
      dir_obj,
      dir_output
    }
  }
];
