const rawDescription = $('get').first().json.title
function cleanDescription(input) {
  let output = input;

  // Remove "Sachin Kumar"
  output = output.replace(/Sachin Kumar/gi, "");

  // Remove "#Sachin_kumar_a1"
  output = output.replace(/#Sachin_kumar_a1/gi, "");

  // Trim extra spaces left behind
  output = output.replace(/\s{2,}/g, " ").trim();

  // Escape for JSON: backslashes, quotes, newlines, carriage returns
  output = output
    .replace(/\\/g, "\\\\")
    .replace(/"/g, '\\"')
    .replace(/\r/g, "\\r")
    .replace(/\n/g, "\\n");

  return output;
}

// âœ… Return one clean item for n8n
return [
  {
    json: {
      description: cleanDescription(rawDescription)
    }
  }
];
