// Captions array (text + texture emoji only)
const captions = [
  "Wait for it... ⚡",
  "You won’t believe this!",
  "The ending is wild !",
  "Hold on... !",
  "Trust me, watch till the end !",
  "This part got me !",
  "Keep watching !",
  "Plot twist ahead !",
  "Didn’t see that coming !",
  "Stay till the last sec !"
];

// Default n = 1
let n = 1;

// items[0].json contains the incoming webhook body when using the Webhook node
if (Array.isArray(items) && items.length && items[0].json) {
  const body = items[0].json;
  if (body.n_atom !== undefined && body.n_atom !== null) {
    const parsed = parseInt(body.n_atom, 10);
    if (!isNaN(parsed)) n = parsed;
  }
  // Support query params if webhook forwarded them under json.query
  if (body.query && body.query.n_atom) {
    const qp = parseInt(body.query.n_atom, 10);
    if (!isNaN(qp)) n = qp;
  }
}

// Clamp n between 1 and captions.length
n = Math.max(1, Math.min(n, captions.length));

function pickRandomUnique(arr, count) {
  const copy = arr.slice();
  const out = [];
  for (let i = 0; i < count; i++) {
    const idx = Math.floor(Math.random() * copy.length);
    out.push(copy.splice(idx, 1)[0]);
  }
  return out;
}

const selected = pickRandomUnique(captions, n);

return [
  {
    json: {
      captions: selected,
      count: selected.length,
      requested_n: n
    }
  }
];
