// Select One To Process (fixed)
// Mode: Run Once for All Items
const rows = $input.all().map(i => i.json || {});

// helper: safe access to other nodes
function safeNodeFirstValue(nodeName) {
  try {
    if (typeof $node === 'undefined') return null;
    const n = $node[nodeName];
    if (!n) return null;
    const first = (typeof n.first === 'function') ? n.first() : (n.items && n.items[0]);
    return first && first.json ? first.json : null;
  } catch (err) {
    return null;
  }
}

// statuses that mean "already processed" (do NOT pick)
const SKIP_STATUSES = new Set([
  'PUBLISHED',
  'DOWNLOADED',
  'PROCESSING',
  'ERROR',
  'CANCELLED'
]);

function isAvailable(row) {
  if (!row) return false;

  // If you have a boolean 'published' column, respect it
  if (row.published === true || String(row.published).toLowerCase() === 'true') return false;

  const s = row.status;
  if (s === undefined || s === null) return true;
  if (typeof s === 'string') {
    const t = s.trim().toUpperCase();
    if (t === '') return true;            // blank = available
    if (SKIP_STATUSES.has(t)) return false; // skip known processed states
    // treat all other strings as available (you can tighten this list if needed)
    return true;
  }
  // non-string status -> treat falsy as available
  return !s;
}

// debug info (optional)
const debug = {
  rowsCount: rows.length,
  sampleRows: rows.slice(0,5),
  SetConfig5_first: safeNodeFirstValue('Set Config5'),
};
console.log('Select One To Process - debug:', debug);

// pick first available row
let chosen = rows.find(isAvailable) || null;

if (!chosen) {
  return [{
    json: {
      debug: 'NO_ROW_SELECTED',
      reason: 'no rows matched the availability criteria',
      rowsCount: debug.rowsCount,
      sampleRows: debug.sampleRows
    }
  }];
}

// resolve filename (fallbacks kept from your original)
let filename = (chosen.filename || chosen.original_name || '').toString().trim();
if (!filename) {
  const code3First = safeNodeFirstValue('Code3');
  if (code3First && code3First.filename) filename = String(code3First.filename).trim();
}

// files dir fallback from Set Config5
let filesDir = '/files';
const setCfg = safeNodeFirstValue('Set Config5');
if (setCfg && setCfg.FILES_DIR) {
  const trimmed = String(setCfg.FILES_DIR).trim();
  if (trimmed) filesDir = trimmed;
}

const baseDir = (filesDir || '/files').replace(/\/+$/, '') || '/files';
const local_path = filename ? `${baseDir}/${filename}` : `${baseDir}/`;

// mark chosen as processing so subsequent runs don't pick it
chosen.local_path = local_path;
chosen.status = 'PROCESSING';

return [{ json: chosen }];

/* Increment attempts from persistent Init Poll Vars so creation_id is preserved */
const prev = $items('Init Poll Vars')[0].json || {};
return [{
  json: {
    creation_id: prev.creation_id,
    attempts: (prev.attempts || 0) + 1,
    max_attempts: prev.max_attempts || 20,
    sleep_sec: prev.sleep_sec || 6
  }
}];

// GetFileLinks (Function) - normalizes input from SFTP list node
const out = [];

for (const item of $input.all()) {
  const rawName = item.json.name || item.json.filename || '';
  const name = rawName.replace(/\r?\n/g, '').trim();

  const path = item.json.path || `/upload/${name}`;
  const fileid = name;            // unique id per file (your filename)
  const filename = name;
  const remote_path = path;
  const created_at = item.json.modifyTime || new Date().toISOString();
  const size = item.json.size || null;

  out.push({
    json: {
      fileid,
      original_name: name,
      filename,
      remote_path,
      created_at,
      size
      // keep minimal; BuildRowObjectForExcel will build the DirectUrl and status
    }
  });
}

return out;

// Collect output rows here
const output = [];

// Loop over all incoming items
for (let i = 0; i < items.length; i++) {
  const item = items[i];        // <-- define `item`
  const data = item.json || {}; // actual JSON payload

  // Read UNAME: prefer the current item, else fallback to Set Config5 node
  // Note: replace "Set Config5" with your node name if different
  const uname =
    (data.UNAME && String(data.UNAME).trim()) ||
    ($node && $node["Set Config5"] && $node["Set Config5"].json && $node["Set Config5"].json.UNAME) ||
    '';

  // Read file fields safely
  const fileid = data.fileid || data.filename || '';
  const filename = data.filename || fileid || '';
  const encodedFilename = encodeURIComponent(filename || '');

  // Build the direct URL & remote path using uname
  const direct_url = `https://files.mohdasif.net/${uname}/${encodedFilename}`;
  const remote_path = data.remote_path || `/upload/${uname}/${filename}`;

  // Compose the row object â€” keys should match your Excel/Sheet column headers
  const row = {
    fileid,
    original_name: filename,
    DirectUrl: direct_url,
    remote_path,
    created_at: data.created_at || new Date().toISOString(),
    size: data.size || null,
    status: data.status || 'unknown',
    published: data.published || false
  };

  // Push as a proper n8n return item
  output.push({ json: row });
}

// Return the final array of items
return output;

return [{
  json: {
    DirectUrl: $items('Select One To Process4')[0]?.json?.DirectUrl || $json.DirectUrl || null,
    status: 'PUBLISHED',
    published_meta: { when: new Date().toISOString() }
  }
}];

// Prepare Cancel/Error Update
// This node now handles both timeouts and API errors.

const selectNode = $items('Select One To Process4')[0]?.json;
const checkStatusNode = $items('IG Check Status1')[0]?.json;
const incAttemptsNode = $items('Inc Attempts1')[0]?.json;

let status = 'CANCELLED - attempts exceeded';
let reason = 'max_attempts_reached';

// Check if this path came from an API error
if (checkStatusNode?.status_code === 'ERROR') {
  status = 'ERROR';
  reason = checkStatusNode.status || 'API error during upload';
}

return [{
  json: {
    DirectUrl: selectNode?.DirectUrl || null,
    status: status,
    cancel_meta: {
      attempts: incAttemptsNode?.attempts || null,
      reason: reason
    }
  }
}];

return [{
  json: {
    creation_id: $item(0).$node['IG Create Container1'].json.id,
    attempts: 0,
    max_attempts: 5,
    sleep_sec: 6
  }
}];
